import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import NameForm from './NameForm';

class App extends Component {
    constructor(props) {
    super(props)
    this.setValid = this.setValid.bind(this);
    this.state = {isValid:true};
    this.error = React.createRef();

    }
    setValid(value) {
        this.setState({isValid:value});
        const error = this.error.current;
        if(this.state.isValid) {
           console.log("getting input!"); 
        } else {
            this.error.current.classList.add("d-none");
        }


    }
    render() {
        // let arr = [];
        // arr.push(
        if(this.state.isValid) {
            return(<div>
                <NameForm setValid={this.setValid} />
                {/* {this.state.isValid ? null : <div>Error</div> } */}

                </div>);
    } else {
        return(
            <div>
            <NameForm setValid={this.setValid} />
        <div id="error" ref={this.error} className="error-text">You made an Error!</div>
        </div> );
    }


    //         if(!this.state.isValid) {
    //             arr.push();
    // }  
    //     return arr;

    }
}

export default App;
